import React from 'react';
import TableViewBtn from '@/components/custom/table/ViewBtn';
import DeleteBtnTable from '@/components/global/deleteBtnTable';
import TableEditBtn from '@/components/global/tableEditBtn';
import { useDelete{moduleName} } from '@/services/management/{moduleName.toLowerCase()}/{moduleName.toLowerCase()}.hook';
import { message } from 'antd';
import { useNavigate } from 'react-router-dom';

const ActionsCell = ({ info }: { info: any }) => {
  const delete{moduleName}Mutation = useDelete{moduleName}();
  const navigate = useNavigate();
  const {moduleName.toLowerCase()}Id = info.row.original.id;

  const handleView = () => {
    navigate(`/{moduleName.toLowerCase()}s/${{moduleName.toLowerCase()}Id}/view`);
  };

  const handleEdit = () => {
    navigate(`/{moduleName.toLowerCase()}/update/${{moduleName.toLowerCase()}Id}`);
  };

  const handleDelete = () => {
    delete{moduleName}Mutation.mutate({moduleName.toLowerCase()}Id, {
      onSuccess: () => {
        message.success(`{moduleName} with ID ${{moduleName.toLowerCase()}Id} deleted successfully.`);
      }
    });
  };

  return (
    <div className='flex gap-2'>
      <TableViewBtn onClick={handleView} moduleName='{moduleName}' />
      <TableEditBtn onClick={handleEdit} moduleName='{moduleName}' />
      <DeleteBtnTable onClick={handleDelete} moduleName='{moduleName}' api={delete{moduleName}Mutation} />
    </div>
  );
};

export default ActionsCell;
