import { Form, Input } from 'antd';
import { SelectCity } from '@/components/global/form/SelectCity';
import { SelectState } from '@/components/global/form/SelectState';
import { FormInstance } from 'antd/lib';

interface Props {
  form: FormInstance;
  handleSubmit: (values: any) => void;
}

const {moduleName}Form = ({ form, handleSubmit }: Props) => {
  return (
    <Form form={form} layout='vertical' onFinish={handleSubmit}>
      <div className='grid md:grid-cols-4 gap-4'>
        <Form.Item label='{moduleName} Name' name='{moduleName.toLowerCase()}_name'>
          <Input type='text' />
        </Form.Item>
        <Form.Item label='Address' name='address'>
          <Input type='text' />
        </Form.Item>
        <Form.Item label='Registration Number' name='registration_number'>
          <Input type='text' />
        </Form.Item>
        <SelectCity />
        <SelectState />
      </div>
    </Form>
  );
};

export default {moduleName}Form;
